package com.example.schoolonline;

import android.app.Activity;
import android.content.Intent;
import android.graphics.Bitmap;
import android.graphics.BitmapFactory;
import android.os.Bundle;
import android.util.Base64;
import android.widget.ImageView;
import android.widget.TextView;

public class CoursePageActivity extends Activity{
	TextView subject, subjectid, subjectinfo, belongto, teacher, assistant, teacheremail, teacherphone;
	ImageView courseimage;
	protected void onCreate(Bundle savedInstanceState) {
		super.onCreate(savedInstanceState);
	    setContentView(R.layout.activity_course_page);
	    courseimage = (ImageView)findViewById(R.id.courseimage);
	    subject = (TextView)findViewById(R.id.subject);
	    subjectid = (TextView)findViewById(R.id.subjectid);
	    subjectinfo = (TextView)findViewById(R.id.subjectinfo);
	    belongto = (TextView)findViewById(R.id.belongto);
	    teacher = (TextView)findViewById(R.id.teacher);
	    assistant = (TextView)findViewById(R.id.assistant);
	    teacheremail = (TextView)findViewById(R.id.teacheremail);
	    teacherphone = (TextView)findViewById(R.id.teacherphone);
	    Intent intent=getIntent();  
	    String temp=intent.getStringExtra("subject");
        subject.setText(temp.toCharArray(), 0, temp.length());
        temp="id:"+intent.getStringExtra("subjectid");
        subjectid.setText(temp.toCharArray(), 0, temp.length());
		temp=intent.getStringExtra("subjectinfo");
        subjectinfo.setText(temp.toCharArray(), 0, temp.length());
        temp=intent.getStringExtra("belongto");
        belongto.setText(temp.toCharArray(), 0, temp.length());
        temp=intent.getStringExtra("teacher");
        teacher.setText(temp.toCharArray(), 0, temp.length());
        temp=intent.getStringExtra("assistant");
        assistant.setText(temp.toCharArray(), 0, temp.length());
        temp=intent.getStringExtra("teacheremail");
        teacheremail.setText(temp.toCharArray(), 0, temp.length());
        temp=intent.getStringExtra("teacherphone");
        teacherphone.setText(temp.toCharArray(), 0, temp.length());
        
        //base64
        String data = new String("");
        temp=intent.getStringExtra("image");
        byte [] baseByte;
        if(temp.equals(""))
        	baseByte = android.util.Base64.decode(data, Base64.DEFAULT);
        else
        	baseByte = android.util.Base64.decode(temp, Base64.DEFAULT);
        Bitmap bitmap = BitmapFactory.decodeByteArray(baseByte, 0, baseByte.length);
        courseimage.setImageBitmap(bitmap);
        courseimage.setAdjustViewBounds(true);
        courseimage.setMaxHeight(200);
        courseimage.setMaxWidth(200);
	 }	 
}
